class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        ans = ["9"] * n

        if k in [1, 3, 9]:
            return "".join(ans)

        if k == 2:
            ans[0] = "8"
            ans[n - 1] = "8"
            return "".join(ans)

        if k == 4:
            ans[0] = "8"
            ans[n - 1] = "8"

            if n > 2:
                ans[1] = "8"
                ans[n - 2] = "8"

            return "".join(ans)

        if k == 5:
            ans[0] = "5"
            ans[n - 1] = "5"
            return "".join(ans)

        if k == 6:
            if n <= 2:
                return "6" * n

            if n % 2:
                ans[0] = "8"
                ans[n - 1] = "8"
                ans[n // 2] = "8"
            else:
                ans[0] = "8"
                ans[n - 1] = "8"
                ans[n // 2] = "7"
                ans[n // 2 - 1] = "7"

            return "".join(ans)

        def div7(num):
            cur = num[-1]
            last =  num[-2]
            for i in range(len(num) - 1, 1 ,-1):
                last = num[i-2] + last[0]
                last = str(int(last) - int(cur) * 2)
                cur = last[1]
            return int(last) % 7 == 0

        if k == 7:
            if n <= 2:
                return "7" * n
            if n % 2:
                for i in range(9, 0, -1):
                    ans[n // 2] = str(i)
                    if div7(ans):
                        return "".join(ans)
            else:
                for i in range(9, 0, -1):
                    ans[n // 2] = str(i)
                    ans[n // 2 - 1] = str(i)
                    if div7(ans):
                        return "".join(ans)

        if k == 8:
            if n <= 6:
                return "8" * n

            ans[0] = "8"
            ans[n - 1] = "8"
            ans[1] = "8"
            ans[n - 2] = "8"
            ans[2] = "8"
            ans[n - 3] = "8"
            return "".join(ans)
