class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n == 1:
            for i in range(9,-1,-1):
                if i % k == 0:
                    return str(i)
        if k == 1 or k == 9 or k == 3:
            return '9' * n
        if k == 2:
            return '8' + '9' * (n-2) + '8'
        if k == 4:
            if n == 2:
                return '88'
            elif n == 3:
                return '888'
            else:
                return '88' + '9' * (n-4) + '88'
        if k == 5:
            return '5' + '9' * (n-2) + '5'
        if k == 6:
            s = 8 + 8 + 9 * (n-2)
            dt = 0
            if (n - 2) & 1:
                if s % 3 == 0:
                    dt = 0
                elif s % 3 == 1:
                    dt = 1
                else:
                    dt = 2
                m = (n - 2) // 2
                return '8' + '9' * m + str(9 - dt) + '9' * m + '8'    
            else:
                if n == 2:
                    return '66'
                m = (n - 4) // 2
                if s % 3 == 0:
                    dt = 0
                elif s % 3 == 1:
                    dt = 2
                elif s % 3 == 2:
                    dt = 1
                return '8' + '9' * m + str(9 - dt) * 2 + '9' * m + '8'    
        if k == 7:
            def chk7(a):
                d = a.copy()
                while len(d) > 2:
                    a = d[-1] * 2
                    d.pop()
                    p = a % 10
                    q = a // 10
                    if d[-1] >= p:
                        d[-1] -= p
                    else:
                        q += 1
                        d[-1] = d[-1] + 10 - p
                    for j in range(len(d) - 2,-1,-1):
                        if d[j] >= q:
                            d[j] -= q
                            break
                        else:
                            d[j] = d[j] + 10 - q
                            q = 1
                if len(d) == 2:
                    return (d[0] * 10 + d[1]) % 7 == 0
                else:
                    return d[0] % 7 == 0
            if n & 1:
                for i in range(9,-1,-1):
                    d = [9] * n
                    d[n // 2] = i
                    if chk7(d):
                        return "".join([str(j) for j in d])
            else:
                for i in range(9,-1,-1):
                    d = [9] * n
                    d[n // 2] = i
                    d[n // 2-1] = i
                    if chk7(d):
                        return "".join([str(j) for j in d])

        if k == 8:
            if n == 2:
                return '88'
            elif n == 3:
                for i in range(998,-1,-1):
                    if i % 8 == 0 and str(i) == str(i)[::-1]:
                        return str(i)
            elif n == 4:
                for i in range(8,-1, -2):
                    for j in range(9,-1,-1):
                        if (i * 1001 + j * 110) % 8 == 0:
                            return str(i * 1001 + j * 110)
            else:
                for i in range(8,-1, -2):
                    for j in range(9,-1,-1):
                        for k in range(9,-1,-1):
                            if (i + j * 10 + k * 100) % 8 == 0:
                                if n == 5:
                                    return str(i * 10001+j*1010+k*100)
                                elif n == 6:
                                    return str(i * 100001+j*10010+k*1100)
                                else:
                                    return str(i)+str(j)+str(k) + '9' * (n-6) + str(i+j*10+k*100)
