class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        }
        if (k == 2) {
            if (n == 1) return "8";
            return "8" + string(n - 2, '9') + "8";
        }
        if (k == 4) {
            if (n <= 4) return string(n, '8');
            return "88" + string(n - 4, '9') + "88";
        }
        if (k == 5) {
            if (n == 1) return "5";
            return "5" + string(n - 2, '9') + "5";
        }
        if (k == 6) {
            if (n <= 2) return string(n, '6');
            if (n % 2 == 1) {
                return "8" + string((n - 2) / 2, '9') + string((n - 2) % 2, '8') + string((n - 2) / 2, '9') + "8";
            }
            return "8" + string((n - 4) / 2, '9') + string(2, '7') + string((n - 4) / 2, '9') + "8";
        }
        if (k == 7){
            if (n <= 2) return string(n, '7');
            string s(n, '9');
            if (n % 2 == 1) {
                while (!f7(s)) {
                    s[n / 2]--;
                }
            }
            else {
                while (!f7(s)) {
                    s[n / 2]--;
                    s[n / 2 - 1]--;
                }
            }
            return s;
        }
        if (k == 8) {
            if (n <= 6) return string(n, '8');
            return "888" + string(n - 6, '9') + "888";
        }
        return "0";
    }

private:
    bool f7(string& s) {
        int n = 0;
        for (char c : s) {
            n = n * 10 + c - '0';
            n %= 7;
        }
        return n == 0;
    }
};
