class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        sys.set_int_max_str_digits(100000)

        v = int("9"*n)

        if v < 100000:
            while v%k != 0 or str(v) != str(v)[::-1]:
                v -= 1
            return str(v)

        if k in [1,3,9]:
            return "9"*n
        if k == 2:
            return "8" + "9"*(n-2)+"8"
        if k == 4:
            return "88" + "9"*(n-4) + "88"
        if k == 5:
            if n == 1:
                return "5"
            return "5" + "9"*(n-2)+"5"
        if k == 6:
            v = int("8" + "9"*(n-2)+"8")
            e = n//2
            while v%k != 0:
                v -= 10**e
                if n%2 == 0:
                    v -= 10**(e-1)
            return str(v)
        if k == 7:
            v = int("9" + "9"*(n-2)+"9")
            e = n//2
            while v%k != 0:
                v -= 10**e
                if n%2 == 0:
                    v -= 10**(e-1)
            return str(v)
        if k == 8:
            v = int("888" + "9"*(n-6)+"888")
            e = n//2
            while v%k != 0:
                v -= 10**e
                if n%2 == 0:
                    v -= 10**(e-1)
            return str(v)
