int MOD[100020][10];
string ans;
int ret[100020];
class Solution {
public:
    string largestPalindrome(int n, int k) {
        for(int i=0;i<10;++i){
            int now = i % k;
            MOD[0][i] = now;
            for(int j=1;j<=n;++j){
                now *= 10;
                now %= k;
                MOD[j][i] = now;
            }
        }
        ans = "";
        for(int i=0;i<n;++i) ret[i] = 9;
        if(k % 2 == 0) {
            ret[0] = ret[n-1] = 8;
            if(k == 4){
                if(n > 2) ret[1] = ret[n-2] = 8;
            }else if(k == 8){
                if(n > 2) ret[1] = ret[n-2] = 8;
                if(n > 4) ret[2] = ret[n-3] = 8;
            }else{
                int now = 0;
                for(int i=0;i<n;++i){
                    if(i == n / 2)continue;
                    if(n % 2 == 0 && i == n/2 - 1) continue;
                    now += MOD[i][ret[i]];
                    now %= k;
                }
                for(int i=9;i>=0;--i){
                    int x = MOD[n/2][i];
                    if(n % 2 == 0) x += MOD[n/2-1][i];
                    x %= k;
                    if((x + now) % k == 0){
                        ret[n/2] = i;
                        if(n % 2 == 0)ret[n/2-1] = i;
                        break;
                    }
                }                
            }
        }else if(k == 5){
            ret[0] = ret[n-1] = 5;
        }else{
            int now = 0;
            for(int i=0;i<n;++i){
                    if(i == n / 2)continue;
                    if(n % 2 == 0 && i == n/2 - 1) continue;
                now += MOD[i][ret[i]];
                now %= k;
            }
            for(int i=9;i>=0;--i){
                int x = MOD[n/2][i];
                if(n % 2 == 0) x += MOD[n/2-1][i];
                x %= k;
                if((x + now) % k == 0){
                    ret[n/2] = i;
                    if(n % 2 == 0)ret[n/2-1] = i;
                    break;
                }
            }
        }
        for(int i=0;i<n;++i) ans += '0' + ret[i];
        return ans;

    }
};
