class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def check(s):
            s = list(map(int,s))
            carry = 0
            for num in s:
                i = carry*10+num
                carry = i%7
            if carry:
                return False
            return True
        if k in [1,3,9]:
            return "9"*n
        if k==2:
            if n == 1:
                return "8"
            s = "9"*(n-2)
            return "8"+s+"8"
        if k==5:
            if n==1:
                return "5"
            s = "9"*(n-2)
            return "5"+s+"5"
        if k==8:
            if n<7:
                return "8"*n
            return "888"+"9"*(n-6)+"888"
        if k==4:
            if n<5:
                return "8"*n
            s = "9"*(n-4)
            return "88"+s+"88"
        if k == 6:
            if n<3:
                return "6"*n
            if n==3:
                return "888"
            if n==4:
                return "8778"
            if n%2:
                i = n//2
                s = "9"*(i-1) + "8"
                return s[::-1]+"8"+s
            else:
                i = n//2
                s = "7"+"9"*(i-2)+"8"
                return s[::-1]+s
        if n<3:
            return "7"*n
        if n%2:
            #odd
            s = "9"*(n//2)
            for i in range(9,-1,-1):
                if check(s+str(i)+s):
                    return s+str(i)+s
        else:
            s = "9"*(n//2 - 1)
            for i in range(9,-1,-1):
                if check(s+str(i)+str(i)+s):
                    return s+str(i)+str(i)+s
