bool is_div7(string &s) {
    int res = 0;
    for (int i = 0; i < s.size(); i++) {
        int x = s[i] - '0';
        res *= 10;
        res += x;
        res %= 7;
    }
    return res == 0;
}
class Solution {
public:
    string largestPalindrome(int n, int k) {
        string ret = "";
        for (int i = 0; i < n; i++) ret += '9';
        if (k == 1) {
            // done
        } else if (k == 2) {
            ret[0] = '8';
            ret[n-1] = '8';
        } else if (k == 3) {
            // done
        } else if (k == 4) {
            // 88
            ret[0] = '8';
            if (n > 1) {
                ret[1] = '8';
                ret[n-2] = '8';
            }
            ret[n-1] = '8';
        } else if (k == 5) {
            ret[0] = '5';
            ret[n-1] = '5';
        } else if (k == 6) {
            if (n == 1) return "6";
            if (n == 2) return "66";
            ret[0] = '8';
            ret[n-1] = '8';
            if (n%2) {
                ret[n/2] = '8';
            } else {
                ret[n/2] = '7';
                ret[n/2-1] = '7';
            }
        } else if (k == 7) {
            char &c = ret[n/2];
            if (n%2) {
                while (!is_div7(ret)) {
                    c--;
                }
            } else {
                char &c2 = ret[n/2-1];
                while (!is_div7(ret)) {
                    c--;
                    c2--;
                    if (c < '0') return "LFSDHJKSDHFLSDHJf";
                }
            }
        } else if (k == 8) {
            ret[0] = '8';
            if (n > 1) {
                ret[1] = '8';
                ret[n-2] = '8';
            }
            if (n > 2) {
                ret[2] = '8';
                ret[n-3] = '8';
            }
            ret[n-1] = '8';
        } else if (k == 9) {
            // done
        }
        return ret;
    }
};
