class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        def cal(s):
            a = 0
            for x in s:
                a = a * 10
                a += x
                a = a % 7
            if a == 0:
                return True
            else:
                return False
        if k == 1:
            return "9" * n
        elif k == 2:
            if n == 1:
                return "8"
            else:
                return "8" + (n - 2) * "9" + "8"
        elif k == 3:
            return "9" * n
        elif k == 4:
            if n == 1:
                return "8"
            elif n == 2:
                return "88"
            elif n == 3:
                return "888"
            else:
                return "88" + (n - 4) * "9" + "88"
        elif k == 5:
            if n == 1:
                return "5"
            else:
                return "5" + (n - 2) * "9" + "5"
        elif k == 6:
            if n == 1:
                return "6"
            elif n == 2:
                return "66"
            else:
                if n % 2 == 0:
                    l = (n - 4) // 2
                    return "8" + l * "9" + "77" + l * "9" + "8"
                else:
                    l = (n - 3) // 2
                return "8" + l * "9" + "8" + l * "9" + "8"
        elif k == 7:
            if n == 1:
                return "7"
            elif n == 2:
                return "77" 
            else:
                s = [9] * n
                for _ in range(10):
                    if cal(s):
                        for i in range(len(s)):
                            s[i] = str(s[i])
                        return ''.join(s)
                    if n % 2 == 0:
                        s[n // 2 - 1] -= 1
                        s[n // 2] -= 1
                    else:
                        s[n // 2] -= 1
        elif k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "888" + (n - 6) * "9" + "888"
        elif k == 9:
            return "9" * n
        return 0
