class Solution {
public:
    bool isDivisibleBy7(string& num) {
    long long remainder = 0;
    for (char c : num) {
        remainder = (remainder * 10 + (c - '0')) % 7;
    }
    return remainder == 0;
    }
    string largestPalindrome(int n, int k) {
        string ans=string(n,'9');
        if(k==1)return ans;
        if(k==2){
            ans[0]=ans[n-1]='8';
            return ans;
        }
        if(k==3 || k==9)return ans;
        if(k==4){
            if(n==1)return "8";
            ans[0]=ans[n-1]='8';
            ans[1]=ans[n-2]='8';
        }
        if(k==5){
             ans[0]=ans[n-1]='5';
            return ans;
        }
        if(k==6){
            if(n==1)return "6";
            if(n==2)return "66";
            ans[0]=ans[n-1]='8';
            int x=(n-1)/2;
            if(n%2)ans[x]=ans[n-1-x]='8';
            else ans[x]=ans[n-1-x]='7';
        }
        if(k==7){
            if(n==1)return "7";
            if(n==2)return "77";
            int x=(n-1)/2;
            while(!isDivisibleBy7(ans)){
                char y=ans[x];
                y--;
                cout<<y;
                ans[x]=ans[n-x-1]=y;
            }
            return ans;
        }
        if(k==8){
            if(n==1)return "8";
            if(n==2)return "88";
            ans[0]=ans[1]=ans[2]=ans[n-1]=ans[n-2]=ans[n-3]='8';
            return ans;
        }
        return ans;
    }
};
