class Solution {
public:
    bool f(const std::string& num) {
    int remainder = 0;
    for (char digit : num) {
        remainder = (remainder * 10 + (digit - '0')) % 7;
    }
    return remainder == 0;
    }
    string largestPalindrome(int n, int k) {
        if(n==1){
            if(k==1 || k==3 || k==9) return "9";
            if(k==2 || k==4 || k==8) return "8";
            if(k==5 || k==6 || k==7) return to_string(k);
        }
        string temp="";
        for(int i=0;i<n;i++) temp+='9';
        if(k==1){
            return temp;
        }
        else if(k==2){
            temp[0]='8';
            temp[n-1]='8';
            return temp;
        }
        else if(k==3){
            return temp;
        }
        else if(k==5){
            temp[n-1]='5';
            temp[0]='5';
            return temp;
        }
        else if(k==9){
            return temp;
        }
        else if(k==6){
            if(n==2){
                return "66";
            }
            else{
                temp[n-1]='8';
                temp[0]='8';
                if(n%2==1){
                    temp[n/2]='8';
                }
                else{
                    temp[n/2-1]='7';
                    temp[n/2]='7';
                }
                return temp;
            }
        }
        else if(k==4){
            temp[n-1]='8';
            temp[n-2]='8';
            temp[0]='8';
            temp[1]='8';
            return temp;
        }
        else if(k==8){
            if(n==2){
                temp[n-1]='8';
                temp[n-2]='8';
                return temp;
            }
            else{
                temp[n-1]='8';
                temp[n-2]='8';
                temp[n-3]='8';
                temp[0]='8';
                temp[1]='8';
                temp[2]='8';
                return temp;
            }
        }
        else{
            if(n%2==1){
                for(char x='9';x>='0';x=x-1){
                    temp[n/2]=x;
                    if(f(temp)) return temp;
                }

            }
            else{
                for(char x='9';x>='0';x=x-1){
                    temp[n/2]=x;
                    temp[n/2-1]=x;
                    if(f(temp)) return temp;
                }
            }
        }
        return "";
    }
};
