class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:

        def is_pal(x):
            inv = 0
            y = x
            while x > 0:
                inv = inv*10 + x%10
                x //= 10
            return inv == y

        def brute(x):
            while x > 0:
                if x % k == 0 and is_pal(x):
                    return str(x)
                x -= 1
            return "0"



        def f(d):
            if k in (1, 3, 9):
                return "9"*d

            if d <= 5:
                return brute(10**d)

            if k == 5:
                return "5" + ("9"*(d - 2)) + "5"

            if k == 6:
                part = "9"*((d - 2) // 2)
                if n % 2 != 0:
                    return "8" + part + "8" + part + "8"
                return "8" + part[:-1] + "77" + part[:-1] + "8"

            if k in (2, 4, 8):
                log2 = int(math.log2(k))
                pref = brute(10**log2)
                return pref + ("9"*(d - 2*log2)) + pref[::-1]

            if k == 7:
                # wtf
                wtf = ["7", "77", "5", "77", "7", "", "4", "44", "6", "44", "4", ""]
                mid = wtf[d % len(wtf) - 1]
                pref = "9"*((d - len(mid)) // 2)
                return pref + mid + pref

        return f(n)
