class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in [1, 3, 9]:
            return n * "9"
        if k == 2:
            if n < 2:
                return "8"
            return "8" + (n-2) * "9" + "8"
        if k == 4:
            if n < 4:
                return n*"8"
            return "88" + (n-4) * "9" + "88"
        if k == 5:
            if n < 2:
                return "5"
            return "5" + (n-2) * "9" + "5"
        if k == 6:
            if n < 3:
                return n * "6"
            if n % 2 == 0:
                return "8" + (n-4)//2*"9" + "77" + (n-4)//2*"9" + "8"
            return "8" + (n-3)//2*"9" + "8" + (n-3)//2*"9" + "8"
        def d7(s):
            a = list(map(int, s))
            while len(a) > 200:
                x = a.pop()
                b = min(len(a), 10)
                y = int("".join(map(str, a[-b:])))
                for _ in range(b):
                    a.pop()
                assert(y >= x+x)
                a.extend(list(map(int, str(y - x - x))))
            return int("".join(map(str, a))) % 7 == 0
        if k == 7:
            if n % 2 == 0:
                for x in range(3, 10):
                    s = (n-2)//2*"9" + 2*str(x) + (n-2)//2*"9"
                    if d7(s):
                        return s
            for x in range(3, 10):
                s = (n-1)//2*"9" + str(x) + (n-1)//2*"9"
                if d7(s):
                    return s
        if k == 8:
            if n < 6:
                return n * "8"
            return "888" + (n-6) * "9" + "888"
