class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        # 特殊的有 1 2 3 4 5 8 9
        if  k == 1:
            return "9" * n
        elif k == 2:
            if n == 1:
                return "8"
            return "8" + "9" * (n - 2) + "8"
        elif k == 3:
            return "9" * n
        elif k == 4:
            if n <= 4:
                return "8" * n
            return "88" + "9" * (n - 4) + "88"
        elif k == 5:
            if n == 1:
                return "5"
            return "5" + "9" * (n - 2) + "5"
        elif k == 8:
            if n <= 5:
                return "8" * n
            return "888" + "9" * (n - 6) + "888"
        elif k == 9:
            return "9" * n
        # 剩下 6 7
        if k == 7:
            if n <= 2:
                return "7" * n
            else:
                # 偶数长
                if n % 2 == 0:
                    a = ["9", "7", "7", "9", "4", "4"]
                    t = n // 2 % 6
                    return "9" * (n // 2 - 1) + a[t] * 2 + "9" * (n // 2 - 1)
                else:
                    a = ["7", "5", "7", "4", "6", "4"]
                    t = n // 2 % 6
                    return "9" * (n // 2) + a[t] + "9" * (n // 2)
        elif k == 6:
            if n <= 2:
                return "6" * n
            else:
                # 偶数长
                if n % 2 == 0:
                    return "8" + "9" * (n // 2 - 2) + "77" + "9" * (n // 2 - 2) + "8"
                else:
                    return "8" + "9" * (n // 2 - 1) + "8" + "9" * (n // 2 - 1) + "8"
