class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1) return string(n, '9');
        if (k == 3) return string(n, '9');
        if (k == 9) return string(n, '9');
        string str;
        string mid = n <= 2 ? "" : string(n - 2, '9');
        if (k == 2) return n == 1 ? "8" : "8" + mid + "8";
        if (k == 5) return n == 1 ? "5" : "5" + mid + "5";
        if (k == 4) {
            str = n == 1 ? "8" : "8" + mid + "8";
            if (n != 1) str[1] = '8', str[n - 2] = '8'; return str;
        }
        if (n == 1) return string(1, '0' + k);
        if (k == 7) str = "9" + mid + "9";
        if (k != 7) str = "8" + mid + "8";
        if (k == 8) {
            if (n >= 2) str[1] = '8', str[n - 2] = '8';
            if (n >= 3) str[2] = '8', str[n - 3] = '8';
            return str;
        }
        int x = n % 2 == 0 ? n / 2 - 1 : n / 2;
        int y = n % 2 == 0 ? n / 2 - 0 : n / 2;
        for (char d = '9'; d > '2'; d--) {
            str[x] = d;
            str[y] = d;
            int mod = 0;
            for (char c: str) mod = (mod * 10 + (c - '0')) % k;
            if (mod == 0) return str;
        }
        return "";
    }
};
