def largestPalindrome7( n: int, k: int) -> str:
    def isPalindrome(num):
        return str(num) == str(num)[::-1]

    def generatePalindrome(half):
        if n % 2 == 0:
            return int(str(half) + str(half)[::-1])
        else:
            return int(str(half) + str(half)[-2::-1])

    if n == 1:
        for i in range(9, 0, -1):
            if i % k == 0:
                return str(i)
        return "0"

    upper = 10 ** ((n + 1) // 2) - 1
    lower = 10 ** ((n - 1) // 2)
    for half in range(upper, lower - 1, -1):
        palindrome = generatePalindrome(half)
        if palindrome % k == 0:
            return str(palindrome)
    return "0"


class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9"* n
        if k == 2:
            if n == 1:
                return "8"
            if n == 2:
                return "88"
            return "8" + "9" *(n - 2 ) + "8"
        if k == 4:
            if n == 1:
                return "8"
            if n == 2:
                return "88"
            if n == 3:
                return "888"
            if n == 4:
                return "8888"
            return "88" + "9" *(n - 4 ) + "88"
        if k == 5:
            if n == 1:
                return "5"
            if n == 2:
                return "55"
            if n == 3:
                return "595"
            return "5" + (n - 2) * "9" + "5"
        if k == 6:
            if n == 1:
                return "6"
            if n == 2:
                return "66"
            if n == 3:
                return "888"
            if n % 2 == 0:
                return "8" + "9" *(( n - 4 ) // 2) + "77" + "9" *(( n - 4 ) // 2)  + "8"
            else:
                return "8" + "9" *(( n - 3 ) // 2) + "8" + "9" *(( n - 3 ) // 2)  + "8"
        if k == 7:
            return largestPalindrome7(n, k)
        if k == 8:
            if n < 6:
                return "8" * n
            else:
                return "888" + "9" * (n - 6 ) + "888"
