class Solution:
    sys.set_int_max_str_digits(100000)

    def largestPalindrome(self, n: int, k: int) -> str:
        k = str(k)
        if k == '1' or k == '3' or k == '9':
            return ('9' * n)
        elif k == '2':
            if n < 3:
                return ('8' * n)
            else:
                return ('8' + '9' * (n - 2) + '8')
        elif k == '4':
            if n <= 4:
                return ('8' * n)
            else:
                return ('88' + '9' * (n - 4) + '88')
        elif k == '5':
            if n < 3:
                return ('5' * n)
            else:
                return ('5' + '9' * (n - 2) + '5')
        elif k == '6':
            if n < 3:
                return ('6' * n)
            elif n == 3:
                return str(888)
            elif n == 4:
                return str(8778)
            else:
                for i in range(9, -1, -1):
                    num_9 = (n - 1) // 2 - 1
                    test = int('8' + '9' * num_9 + str(i) * (n-2 - 2 * num_9) + '9' * num_9 + '8') 
                    if test % 6 == 0:
                        return str(test)
        elif k == '7':
            if n < 3:
                return ('7' * n)
            else:
                for i in range(9, -1, -1):
                    num_9 = (n - 1) // 2
                    test = int('9' * (num_9) + str(i) * (n - 2 * num_9) + '9' * (num_9)) 
                    if test % 7 == 0:
                        return str(test)
        else:
            # k = 8
            if n < 7:
                return ('8' * n)
            else:
                return ('8' * 3 + '9' * (n - 6) + '8' * 3)
