import sys
sys.set_int_max_str_digits(100001)

class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        number = ['0'] * n
        if k == 1 or k == 3 or k == 9:
            for i in range(n):
                number[i] = '9'
        elif k == 2:
            number[0] = '8'
            number[-1] = '8'
            for i in range(1, n - 1):
                number[i] = '9'
        elif k == 5:
            number[0] = '5'
            number[-1] = '5'
            for i in range(1, n - 1):
                number[i] = '9'
        elif k == 4:
            number[0], number[-1] = '8', '8'
            if n > 2:
                number[1] = '8'
            if n > 3:
                number[-2] = '8'
            for i in range(2, n - 2):
                number[i] = '9'
        elif k == 8:
            number[0], number[-1] = '8', '8'
            if n > 2:
                number[1] = '8'
            if n > 3:
                number[-2] = '8'
            if n > 4:
                number[2] = '8'
            if n > 5:
                number[-3] = '8'
            for i in range(3, n - 3):
                number[i] = '9'
        elif k == 6:
            if n < 3:
                for i in range(n):
                    number[i] = '6'
            else:
                number[0], number[-1] = '8', '8'
                for i in range(1, n - 1):
                    number[i] = '9'
                if n % 2 == 1:
                    mid = n // 2
                    number[mid] = '8'
                else:
                    mid = n // 2
                    number[mid], number[mid - 1] = '7', '7'
        elif k == 7:
            if n < 3:
                for i in range(n):
                    number[i] = '7'
            else:
                for i in range(n):
                    number[i] = '9'
                mid = n // 2
                if n % 2 == 1:
                    for digit in '9876543210':
                        number[mid] = digit
                        num = ''.join(number)
                        if int(num) % 7 == 0:
                            return num
                else:
                    for digit in '9876543210':
                        number[mid] = number[mid - 1] = digit
                        num = ''.join(number)
                        if int(num) % 7 == 0:
                            return num

        return ''.join(number)
