class Solution {
public:

    bool test(string &s, int k){
        int i = 0, carry = 0;
        while(i < s.size()){
            carry = carry * 10 + (s[i] - '0');
            carry %= k;
            i++;
        }
        if(carry == 0) return true;
        return false;
    }

    string largestPalindrome(int n, int k) {
        string res = "";
        if(k == 1){
            for(int i = 0; i < n; i++) res += "9";
            return res;
        } else if(k == 2){
            if(n == 1) return "8";
            else if(n == 2) return "88";
            else {
                res += "8";
                for(int i = 0; i < n - 2; i++) res += "9";
                res += "8";
            }
            return res;
        } else if(k == 3 || k == 9){
            for(int i = 0; i < n; i++) res += "9";
            return res;
        } else if(k == 4){
            if(n == 1) return "8";
            else if(n == 2) return "88";
            else if(n == 3) return "888";
            else if(n == 4) return "8888";
            res += "88";
            for(int i = 0; i < n - 4; i++) res += "9";
            res += "88";
            return res;
        } else if(k == 5){
            if(n == 1) return "5";
            else {
                res += "5";
                for(int i = 0; i < n - 2; i++) res += "9";
                res += "5";
                return res;
            }
        } else if(k == 6){
            // not checked
            if(n == 1) return "6";
            else if(n == 2) return "66";
            else if(n == 3) return "888";
            else {
                int count = 0;
                res += "8";
                count += 8;
                for(int i = 0; i < (n - 3) / 2; i++){
                    res += "9", count += 9;
                }
                count *= 2;
                for(int x = 9; x >= 0; x--){
                    if(n % 2 == 1 && (count + x) % 3 == 0){
                        res += (char)('0' + x);
                        break;
                    }
                    if(n % 2 == 0 && (count + 2 * x) % 3 == 0){
                        res += (char)('0' + x);
                        res += (char)('0' + x);
                        break;
                    }
                }
                for(int i = 0; i < (n - 3) / 2; i++){
                    res += "9";
                }
                res += "8";
                return res;
            }
            // 6 6
            // 8 8 8
            // 8 7 7 8
            // 8 9 x 9 8
        } else if(k == 7){
            for(int i = 0; i < n; i++) res += "9";
            for(char c = '9'; c >= '0'; c--){
                if(n % 2 == 0){
                    res[n / 2 - 1] = c;
                    res[n / 2] = c;
                    if(test(res, 7)) return res;
                } else {
                    res[n / 2] = c;
                    if(test(res, 7)) return res;
                }
            }
        } else if(k == 8){
            if(n <= 6){
                for(int i = 0; i < n; i++) res += "8";
                return res;
            }
            res += "888";
            for(int i = 0; i < n - 6; i++) res += "9";
            res += "888";
            return res;
        }

        return "";
    }
};
