class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        """

        :param n:
        :param k:
        :return:
        """

        def is_divisible_by_7_large_number(n_str):
            current_value = 0

            for char in n_str:
                digit = int(char)

                current_value = (current_value * 10 + digit) % 7

            return current_value == 0

        if k == 1:
            return '9' * n
        if k == 2:
            if n == 1:
                return '8'
            return '8' + '9' * (n-2) + '8'
        if k == 3:
            return '9' * n
        if k == 4:
            if n < 5:
                return '8' * n
            return '88' + '9' * (n - 4) + '88'
        if k == 5:
            if n == 1:
                return '5'
            return '5' + '9' * (n-2) + '5'
        if k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            if n %2 == 1:
                ncnt = (n-2) // 2
                return '8' + '9' * ncnt + '8' + '9' * ncnt + '8'
            else:
                ncnt = (n-4) // 2
                return '8' + '9' * ncnt + '77' + '9' * ncnt + '8'
        todo = [11, 22, 33, 44, 55, 66,77,88,99]
        todo.reverse()
        if k == 7:
            if n <= 2:
                return '7' * n
            if n % 2 == 0:
                ncnt = (n -2) // 2
                for i in todo:
                    n1 = '9'* ncnt + str(i) + '9'* ncnt
                    if is_divisible_by_7_large_number(n1):
                        return n1
            else:
                ncnt = (n - 2) // 2 + 1
                for i in range(9, 0, -1):
                    n1 = '9'* ncnt + str(i) + '9'* ncnt
                    if is_divisible_by_7_large_number(n1):
                        return n1

        if k == 8:
            if n == 1:
                return '8'
            if n == 2:
                return '88'
            if n == 3:
                return '888'
            if n == 4:
                return '8888'
            if n == 5:
                return '88888'
            if n == 6:
                return '888888'
            return '888' + '9' * (n-6) + '888'
        if k == 9:
            return '9' *n
