class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:

        digits = ["9" for i in range(n)]
        if k == 2:
            digits[0] = "8"
            digits[-1] = "8"
        if k == 4:
            if n <= 2:
                digits[0] = "8"
                digits[-1] = "8"
            else:
                digits[0] = "8"
                digits[1] = "8"
                digits[-1] = "8"
                digits[-2] = "8"
        if k == 5:
            digits[0] = "5"
            digits[-1] = "5"
        if k == 8:
            if n <= 2:
                digits[0] = "8"
                digits[-1] = "8"
            elif n <= 4:
                digits[0] = "8"
                digits[1] = "8"
                digits[-1] = "8"
                digits[-2] = "8"
            else:
                digits[0] = "8"
                digits[1] = "8"
                digits[2] = "8"
                digits[-1] = "8"
                digits[-2] = "8"
                digits[-3] = "8"
        if k == 7:
            result_num = 1
            for i in range(n):
                result_num *= 10
                result_num %= 7
            result_num -= 1
            if n%2 == 1:
                mid = n // 2
                tmp = 10 ** mid % 7

                for i in range(9):
                    if (result_num - i*tmp)%7 == 0:
                        digits[mid] = str(9-i)
                        break
            else:
                mid = n // 2 - 1
                tmp = 10 ** mid * 11 % 7

                for i in range(9):
                    if (result_num - i*tmp)%7 == 0:
                        digits[mid] = str(9-i)
                        digits[mid+1] = str(9-i)
                        break
        if k == 6:
            if n <= 2:
                digits[0] = "6"
                digits[-1] = "6"
            else:
                digits[0] = "8"
                digits[-1] = "8"
                if n%2 == 1:
                    mid = n // 2
                    digits[mid] = "8"
                    #print(mid)
                    #print(digits)

                else:
                    mid = n // 2 - 1
                    digits[mid] = "7"
                    digits[mid+1] = "7"


        return "".join(digits)
