class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s = "";
        if(n == 1){
            for(int i = 9; i >= 1; i--) if(i % k == 0){
                s += '0' + i;
                return s;
            }
        }
        if(k == 1 || k == 3 || k == 9){
            for(int i = 0; i < n; i++) s += '9';
        }
        else if(k == 2){
            s += '8';
            for(int i = 1; i <= n-2; i++) s += '9';
            s += '8';
        }
        else if(k == 5){
            s += '5';
            for(int i = 1; i <= n-2; i++) s += '9';
            s += '5';
            return s;
        }
        else if(k == 4){
            if(n == 2) return s = "88";
            if(n == 3) return s = "888";
            s = "88";
            for(int i = 0; i < n-4; i++) s += '9';
            s += "88"; 
        }
        else if(k == 6){
            if(n == 2) return s = "66";
            if(n == 4) return s = "8778";
            if(n % 2 == 1){
                int x = 9 * (n - 3) + 2 * 8;
                x = x % 3;
                x = 9 - x;
                s = "8";
                for(int i = 0; i < (n-3)/2; i++) s += '9';
                s += '0' + x;
                for(int i = 0; i < (n-3)/2; i++) s += '9';
                s += "8"; 
            }
            else{
                int x = 9 * (n - 4)/2 +  8;
                x = x % 3;
                x = 9 - x;
                s = "8";
                for(int i = 0; i < (n-4)/2; i++) s += '9';
                s += '0' + x;
                s += '0' + x;
                for(int i = 0; i < (n-4)/2; i++) s += '9';
                s += "8"; 
            }
        }
        else if(k == 8){
            if(n == 2) return s = "88";
            if(n == 3) return s = "888";
            if(n == 4) return s = "8888";
            if(n == 5) return s = "88888";
            s = "888";
            for(int i = 0; i < n-6; i++) s += '9';
            s += "888"; 
        }
        else if(k == 7){
            for(int i = 9; i >= 0; i--){
                s = "";
                for(int i = 0; i < n; i++) s += '9';
                if(n % 2 == 1) s[(n-1)/2] = '0' + i;
                else{
                    s[n/2] = '0' + i;
                    s[n/2-1] = '0' + i;
                }
                int mod7 = 0;
                int j = n-1, sgn = 1;
                while(j >= 0){
                    int num = 0;
                    for(int k = max(0, j-2); k <= j; k++){
                        num *= 10;
                        num += s[k] - '0';
                    } 
                    //cout << num << endl;
                    mod7 += num * sgn;
                    //cout << "mod7 " << mod7 << endl;
                    sgn = -sgn;
                    j -= 3;
                }
                if(mod7 % 7 == 0) return s;
                //cout << endl;
            }
        }
        return s;
    }
};
