#include <vector>
#include <string>
#include <cstring>

class Solution {
    int dp[100001][10];
    int mul[100001];

    bool possible(int i, int j, int m, int k) {
        if (i > j) return m == 0;
        m %= k;
        int &ans = dp[i][m];
        if (ans != -1) return ans;
        ans = false;
        for (int d = 9; d >= 0; d--) {
            int newM = (1LL * mul[i] * d + 1LL * mul[j] * d+m) % k;
            if (i == j) newM = (1LL * mul[i] * d+m) % k;
            if (possible(i + 1, j - 1, newM, k)) {
                ans = true;
                break;
            }
        }
        return ans;
    }

    void traverse(int i, int j, int m, int k, std::string &ans) {
        m %= k;
        while (i <= j) {
            for (int d = 9; d >= 0; d--) {
                int newM = (1LL * mul[i] * d + 1LL * mul[j] * d+m) % k;
                if (i == j) newM = (1LL * mul[i] * d+m) % k;
                if (possible(i + 1, j - 1, newM, k)) {
                    ans[i] = ans[j] = '0' + d;
                    i++;
                    j--;
                    m = newM;
                    break;
                }
            }
        }
    }

public:
    std::string largestPalindrome(int n, int k) {
        memset(dp, -1, sizeof(dp));
        mul[n - 1] = 1;
        for (int i = n - 2; i >= 0; i--) {
            mul[i] = (1LL * mul[i + 1] * 10) % k;
        }

        int start = -1;
        int modulo;
        for (int i = 9; i > 0; i--) {
            int newM = (1LL * mul[0] * i + 1LL * mul[n - 1] * i) % k;
            if (n == 1) newM = (1LL * mul[0] * i) % k;
            if (possible(1, n - 2, newM, k)) {
                start = i;
                modulo = newM;
                break;
            }
        }

        if (start == -1) return "-1";

        std::string ans(n, ' ');
        ans[0] = ans[n - 1] = '0' + start;
        traverse(1, n - 2, modulo, k, ans);
        return ans;
    }
};
