import sys
sys.set_int_max_str_digits(0)
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n == 1:
            ans = 9
            while ans % k != 0:
                ans -= 1
            return str(ans)
        if k == 1:
            return '9' * n
        if k == 2:
            return '8' + ('9' * (n-2)) + '8'
        if k == 3 or k == 9:
            return '9' * n
        if k == 4:
            ans = ['9' for _ in range(n)]
            for i in (0,1,-1,-2):
                ans[i] = '8'
            return ''.join(ans)
        if k == 5:
            return '5' + ('9' * (n-2)) + '5'
        if k == 6:
            if n == 2:
                return '66'
            if n % 2 == 0:
                middle = '9' * ((n-4) // 2)
                return '8' + middle + '77' + middle + '8'
            else:
                middle = '9' * ((n-3) // 2)
                return '8' + middle + '8' + middle + '8'
        if k == 7:
            if n == 2:
                return '77'
            if n % 2 == 0:
                middle = '9' * ((n-2) // 2)
                center = 9
                while int(middle + str(center) * 2 + middle) % 7 != 0:
                    center -= 1
                return middle + str(center) * 2 + middle
            else:
                middle = '9' * ((n-1) // 2)
                center = 9
                while int(middle + str(center) + middle) % 7 != 0:
                    center -= 1
                return middle + str(center) + middle
        if k == 8:
            if n == 2:
                return '88'
            ans = ['9' for _ in range(n)]
            for i in (0,1,2,-1,-2,-3):
                ans[i] = '8'
            return ''.join(ans)
