#include <bits/stdc++.h>
using namespace std;
#define ll long long
#define ld long double
#define all(x) (x).begin(), (x).end()
#define INF 2e18
#define vi  vector<int>
#define vb  vector<bool>
#define vc  vector<char>
#define vvi vector<vector<int>>
#define vvc vector<vector<char>> 
#define vvb vector<vector<bool>> 
#define vpi vector<pair<int,int>>
#define ff first
#define ss second
#define pb push_back
#define ppb pop_back
#define pqb priority_queue<int>
#define pqs priority_queue<int,vi,greater<int> >
#define setbits(x) __builtin_popcountll(x)
#define MOD 1e9+7

class Solution {
public:

    bool divisibleBy7(const vector<char> &arr) {
        int num_mod_7 = 0;
        for (char digit : arr) {
            num_mod_7 = (num_mod_7 * 10 + (digit - '0')) % 7;
        }
        return num_mod_7 == 0;
    }

    string largestPalindrome(int n, int k) {
        string ans;
        if(k==1 || k==3 || k==9){
            while(n--)ans.pb('9');
        }else if(k==2){
            if(n==1)ans="8";
            else if(n==2)ans="88";
            else {
                ans.pb('8');
                n-=2;
                while(n--)ans.pb('9');
                ans.pb('8');
            }
        }else if(k==4){
            if(n<=4){
                while(n--)ans.pb('8');
            }else{
                ans.pb('8');
                ans.pb('8');
                n-=4;
                while(n--)ans.pb('9');
                ans.pb('8');
                ans.pb('8');

            }
        }else if(k==5){
            if(n==1)ans="5";
            else if(n==2)ans="55";
            else {
                ans.pb('5');
                n-=2;
                while(n--)ans.pb('9');
                ans.pb('5');
            }

        }else if(k==6){
            if(n==1)ans="6";
            else if(n==2)ans="66";
            else if(n==3)ans="888";
            else if(n==4)ans="8778";
            else{
                int t=n/2 - 1;
                if(n%2==0)t-=1;
                ans.pb('8');
                int t1=t;
                while(t1--)ans.pb('9');

                int rem=(16 + (t*2*9))%3;

                if(n&1){
                    int dig;
                    for(int i=9;i>=1;i--){
                        int left=i%3;
                        if((left + rem)%3==0){
                            dig=i;
                            break;
                        }
                    }
                    ans.pb(dig+'0');
                }else{
                    int dig;
                    for(int i=9;i>=1;i--){
                        int left=(2*i)%3;
                        if((left + rem)%3==0){
                            dig=i;
                            break;
                        }
                    }
                    ans.pb(dig+'0');
                    ans.pb(dig+'0');
                }

                int t2=t;
                while(t2--)ans.pb('9');
                ans.pb('8');
            }
        }else if(k==7){
            if(n==1)ans="7";
            else if(n==2)ans="77";
            else {

                int t=n/2;
                if(n%2==0)t-=1;
                int t1=t;
                vector<char> vec(n);
                for(int i=0;i<t;i++)vec[i]='9';
                for(int i=n-1;i>=n-t;i--)vec[i]='9';
                if(n&1){
                    for(int d=9;d>=0;d--){
                        vec[t]=d+'0';
                        if(divisibleBy7(vec)){
                            break;
                        }
                    }
                }else{
                    for(int d=9;d>=0;d--){
                        vec[t]=d+'0';
                        vec[t+1]=d+'0';
                        if(divisibleBy7(vec)){
                            break;
                        }
                    }
                }
                for(auto &it : vec)ans.pb(it);
            }
        }else if(k==8){
            if(n<=6){
                while(n--)ans.pb('8');
            }else{
                ans.pb('8');
                ans.pb('8');
                                ans.pb('8');
                n-=6;
                while(n--)ans.pb('9');
                ans.pb('8');
                ans.pb('8');
                                ans.pb('8');
            }
        }
        return ans;
    }
};
