class Solution {
public:

    bool div_7(string num) {
        int n = num.length();
        int alternatingSum = 0;
        int sign = 1;

        // Traverse the number in blocks of three digits from right to left
        for (int i = n - 1; i >= 0; i -= 3) {
            int block = 0;
            // Form the block of up to 3 digits
            for (int j = max(0, i - 2); j <= i; j++) {
                block = block * 10 + (num[j] - '0');
            }

            alternatingSum += sign * block;
            sign = -sign;
        }

        return (alternatingSum % 7) == 0;
    }
    string largestPalindrome(int n, int k) {
        string ans = "";
        if(k==1){
            for(int i = 0; i < n; i++) ans += "9";
        }
        else if(k==2){
            if(n==1) return "8";
            ans = "8";
            for(int i = 0; i < n-2; i++){
                ans += "9";
            }
            ans += "8";

        }
        else if(k==3){
            for(int i = 0; i < n; i++) ans += "9";
        }
        else if(k==4){
            if(n==1) return "8";
            else if(n==2) return "88";
            else if(n==3) return "888";
            ans = "88";
            for(int i = 0; i < n-4; i++){
                ans+="9";
            }
            ans += "88";
        }
        else if(k==5){
            if(n==1) return "5";
            else if(n==2) return "55";
            ans = "5";
            for(int i = 0; i < n-2; i++){
                ans+="9";
            }
            ans += "5";

        }
        else if(k==6){
            if(n==1) return "6";
            else if(n==2) return "66";
            // 89...9[]9...98
            if(n%2==1){
                int count_9 = n-3;
                int total = count_9*9+8*2;
                int mid = 9;
                while((total+mid)%3!=0) mid--;
                ans = "8";
                for(int i = 0; i < count_9/2; i++){
                    ans += "9";
                }
                ans += to_string(mid);
                for(int i = 0; i < count_9/2; i++){
                    ans += "9";
                }
                ans+="8";
            }
            else{
                int count_9 = n-4;
                int total = count_9*9+8*2;
                int mid = 9;
                while((total+2*mid)%3!=0) mid--;
                ans = "8";
                for(int i = 0; i < count_9/2; i++){
                    ans += "9";
                }
                ans += to_string(mid)+to_string(mid);
                for(int i = 0; i < count_9/2; i++){
                    ans += "9";
                }
                ans+="8";
            }
        }
        else if(k==7){
            if(n==1) return "7";
            else if(n==2) return "77";
            if(n%2==1){
                int count_9 = n-1;
                int mid = 9;
                string string_9 = "";
                for(int i = 0; i < count_9/2; i++) string_9 += "9";

                while(!div_7(string_9+to_string(mid)+string_9)){
                    mid--;
                }
                ans = string_9+to_string(mid)+string_9;

            }
            else{
                int count_9 = n-2;
                int mid = 9;
                string string_9 = "";
                for(int i = 0; i < count_9/2; i++) string_9 += "9";
                while(!div_7(string_9+to_string(mid)+to_string(mid)+string_9)){
                    mid--;
                }
                ans = string_9+to_string(mid)+to_string(mid)+string_9;
            }
        }
        else if(k==8){
            if(n==1) return "8";
            else if(n==2) return "88";
            else if(n==3) return "888";
            else if(n==4) return "8888";
            else if(n==5) return "88888";
            else if(n==6) return "888888";
            ans = "888";
            for(int i = 0; i < n-6; i++){
                ans += "9";
            }
            ans += "888";
        }
        else if(k==9){
            for(int i = 0; i < n; i++) ans += "9";
        }
        return ans;

    }
};
