class Solution(object):
    def largestPalindrome(self, n, k):
        """
        :type n: int
        :type k: int
        :rtype: str
        """
        if k == 1 or k == 3 or k == 9:
            return "9" * n
        if k == 4:
            if n <= 4:
                return "8" * n
            else:
                return "88" + "9" * (n-4)+ "88"
        if k == 8:
            if n <= 6:
                return "8" * n
            else:
                return "888" + "9" * (n - 6) + "888"

        if k == 2:
            if n == 1:
                return "8"
            return "8" + "9" * (n-2) + "8"

        if k == 5:
            if n == 1:
                return "5"
            return "5" + "9" * (n - 2) + "5"

        if k == 6:
            if n == 1:
                return "6"
            if n == 2:
                return "66"
            if n % 2 == 1:
                return "8" + "9" * ((n-3) / 2) + "8" + "9" * ((n-3) / 2)+ "8"
            else:
                return "8" + "9" * ( (n-4)/2 ) + "77" + "9" * ( (n-4)/2 ) + "8"

        if k == 7:
            if n == 1:
                return "7"
            if n == 2:
                return "77"
            if n % 2 == 1:
                for i in range(9, -1, -1):
                    s = "9" * ((n-1)/2) + str(i) + "9" * ((n-1)/2)
                    if int(s) % 7 == 0:
                        return s
            else:
                for i in range(9, -1, -1):
                    s = "9" * ((n-2)/2) + str(i) + str(i) + "9" * ((n-2)/2)
                    if int(s) % 7 == 0:
                        return s
        return "3" * 2
