class Solution {
    vector<int>md;
    int find(int st,int en,vector<vector<int>>&dp,int n,int k,int modreq)
    {
        // cout<<"st="<<st<<" en="<<en<<" modreq="<<modreq<<endl;
        if(st>en)
        {
            if(modreq==0) return 0;
            return -2;
        }
        if(dp[st][modreq]!=-1) return dp[st][modreq];
        if(st==en) 
        {
            for(int i=9;i>=0;i--) 
            {
                if((i*md[st])%k==modreq) 
                {
                    // cout<<i*md[st]<<" "<<modreq<<":"<<i<<endl;
                    return dp[st][modreq]=i;
                }
            }
            return dp[st][modreq]=-2;
        }
        for(int i=9;i>=0;i--)
        {
            if(st==0 && i==0) continue;
            int p=md[st]*i+md[en]*i;
            int nmod=((modreq-p)%k+k)%k;
            int ok=find(st+1,en-1,dp,n,k,nmod);
            if(ok!=-2)
            {
                // cout<<"st="<<st<<" en="<<en<<" modreq="<<modreq<<" nmod="<<nmod;
                // cout<<" ok="<<ok<<" i="<<i<<endl;
                return dp[st][modreq]=i;
            }
        }
        return dp[st][modreq]=-2;
    }
public:
    string largestPalindrome(int n, int k) {
        vector<vector<int>>dp(n,vector<int>(k,-1));
        md.resize(n,1);
        for(int i=n-2;i>=0;i--)
        {
            md[i]=md[i+1]*10;
            md[i]%=k;
        }
        // for(auto i:md) cout<<i<<" ";
        // cout<<endl;
        int p=find(0,n-1,dp,n,k,0);
        if(p==-2) return "-1";
        string ans="";
        int curmod=0;
        for(int i=0;i<n/2;i++)
        {
            int d=dp[i][curmod];
            ans+=to_string(d);
            curmod-=(md[i]+md[n-1-i])*d%k;
            curmod%=k;
            curmod+=k;
            curmod%=k;
        }
        if(n%2==1)
        {
            int in=n/2;
            ans+=to_string(dp[in][curmod]);
        }
        int in=n/2-1;
        while(in>=0)
        {
            ans+=ans[in];
            in-=1;
        }
        return ans;

    }
};
