class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s(n,'0');
        if(k==1){
            for(int i=0;i<n;++i)
                s[i]='9';
            return s;
        }
        if(k==2){
            for(int i=0,j=n-1;i<=j;++i,--j){
                if(i==0){
                    s[i]=s[j]='8';
                }else{
                    s[i]=s[j]='9';
                }
            }
            return s;
        }
        if(k==3){
            for(int i=0;i<n;++i)
                s[i]='9';
            return s;
        }
        if(k==4){
            for(int i=0,j=n-1;i<=j;++i,--j){
                if(i<=1){
                    s[i]=s[j]='8';
                }else{
                    s[i]=s[j]='9';
                }
            }
            return s;
        }
        if(k==5){
            for(int i=0,j=n-1;i<=j;++i,--j){
                if(i==0){
                    s[i]=s[j]='5';
                }else{
                    s[i]=s[j]='9';
                }
            }
            return s;
        }
        if(k==6){
            if(n==1){
                return "6";
            }
            if(n==2){
                return "66";
            }
            if(n%2==0){
                for(int i=0,j=n-1;i<=j;++i,--j){
                    if(i==0){
                        s[i]=s[j]='8';
                    }else if(i+1==j){
                        s[i]=s[j]='7';
                    }else{
                        s[i]=s[j]='9';
                    }
                }
            }else{
                for(int i=0,j=n-1;i<=j;++i,--j){
                    if(i==0||i==j){
                        s[i]=s[j]='8';
                    }else{
                        s[i]=s[j]='9';
                    }
                }
            }
            return s;
        }
        if(k==7){
            int f=0;
            for(int i=0;i<n;++i){
                s[i]='9';
                f=(f*10+9)%7;
            }
            if(n%2==1){
                int del=1;
                int x=n/2;
                for(int i=0;i<x;++i){
                    del=del*10%7;
                }
                while(f){
                    f=(f-del+7)%7;
                    --s[x];
                }
            }else{
                int del=11%7;
                int x=n/2-1;
                int y=n/2;
                for(int i=0;i<x;++i){
                    del=del*10%7;
                }
                while(f){
                    --s[x];
                    --s[y];
                    f=(f-del+7)%7;
                }
            }
            return s;
        }
        if(k==8){
            for(int i=0,j=n-1;i<=j;++i,--j){
                if(i<=2){
                    s[i]=s[j]='8';
                }else{
                    s[i]=s[j]='9';
                }
            }
        }
        if(k==9){
            for(int i=0;i<n;++i)
                s[i]='9';
            return s;
        }
        return s;
    }
};
