class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return "9"*n
        if k == 2:
            return "8" if n == 1 else "8" + "9"*(n-2) + "8"
        if k == 3 or k == 9:
            return "9"*n
        if k == 4:
            return "8"*n if n <= 3 else "88" + "9"*(n-4) + "88"
        if k == 5:
            return "5" if n == 1 else "5" + "9"*(n-2) + "5"
        if k == 6:
            if n <= 2:
                return "6"*n
            if n % 2 == 0:
                half = (n - 4) // 2
                return "8" + "9"*half + "7"*2 + "9"*half + "8"
            else:
                half = (n - 3) // 2
                return "8" + "9"*half + "8" + "9"*half + "8"
        if k == 8:
            return "8"*n if n <= 5 else "888" + "9"*(n-6) + "888"
        if k == 7:
            rem = n % 12
            if rem == 1 or rem == 5:
                half = (n - 1) // 2
                return "9"*half + "7" + "9"*half
            if rem == 2 or rem == 4:
                half = (n - 2) // 2
                return "9"*half + "77" + "9"*half
            if rem == 3:
                half = (n - 1) // 2
                return "9"*half + "5" + "9"*half
            if rem == 0 or rem == 6:
                return "9"*n
            if rem == 7 or rem == 11:
                half = (n - 1) // 2
                return "9"*half + "4" + "9"*half
            if rem == 8 or rem == 10:
                half = (n - 2) // 2
                return "9"*half + "44" + "9"*half
            if rem == 9:
                half = (n - 1) // 2
                return "9"*half + "6" + "9"*half
