class Solution {
public:
    string largestPalindrome(int n, int k) {
        string s;
        if(n==1){
            if(k==1||k==3||k==9){
                return "9";
            }
            if(k==2||k==4||k==8){
                return "8";
            }
            if(k==5){
                return "5";
            }
            if(k==7){
                return "7";
            }
            if(k==6){
                return "6";
            }
        }
        if(n==2){
            if(k==1||k==3||k==9){
                return "99";
            }
            if(k==2||k==4||k==8){
                return "88";
            }
            if(k==5){
                return "55";
            }
            if(k==7){
                return "77";
            }
            if(k==6){
                return "66";
            }
        }
        for(int i=0;i<n;i++){
            s+='#';
        }
        if(k==1||k==3||k==9){
            for(int i=0;i<n;i++){
                s[i]='9';
            }
            return s;
        }
        if(k==2||k==4||k==8){
            s[0]=s[n-1]='8';
            if(k>2){
                s[1]=s[n-2]='8';
            }
            if(k==8){
                s[2]=s[n-3]='8';
            }
            for(int i=1;i<n-1;i++){
                if(s[i]=='#') s[i]='9';
            }
            return s;
        }
        if(k==5){
            s[0]=s[n-1]='5';
            for(int i=1;i<n-1;i++){
                s[i]='9';
            }
            return s;
        }
        if(k==6){
            s[0]=s[n-1]='8';
        }
        if(k==7){
            s[0]=s[n-1]='9';
        }
        for(int i=1;i<n/2 && n-i-1>n/2;i++){
            s[i]=s[n-1-i]='9';
        }
            int mod=0;
            for(int i=0;i<n;i++){
                mod=mod*10%k;
                if(s[i]!='#'){
                    mod+=(s[i]-'0');
                    mod%=k;
                }
            }
            int tphalf=1;
            for(int i=0;i<(n-1)/2;i++){
                (tphalf*=10)%=k;
            }
            if(n%2){
                for(int i=9;i>=0;i--){
                    if((mod+tphalf*i)%k==0){
                        s[n/2]=char('0'+i);
                        return s;
                    }
                }
            }
            else{
                for(int i=9;i>=0;i--){
                    if((mod+tphalf*(10*i+i))%k==0){
                        s[n/2-1]=s[n/2]=char('0'+i);
                        return s;
                    }
                }
            }
        return "";
    }
};
