class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:

        def isdivisible7(num):
            n = len(num)
            if (n == 0 and num[0] == '\n'):
                return 1

            if (n % 3 == 1) :
                num = "00" + str(num) 
                n += 2

            elif (n % 3 == 2) :
                num = "0" + str(num) 
                n += 1

            GSum = 0
            p = 1
            i = n-1
            while i>=0 :
                group = 0
                group += ord(num[i]) - ord('0')
                i -= 1
                group += (ord(num[i]) - ord('0')) * 10
                i -= 1
                group += (ord(num[i]) - ord('0')) * 100

                GSum = GSum + group * p

                p *= (-1)
                i -= 1

            return (GSum % 7 == 0)
        #1,3,9 (then 6) - 5 - 2->4->8, fuck 7
        ans = ["9"] * n
        sm = 9 * n  

        if k in (1,3,9):
            return "".join(ans)

        elif k == 2:
            ans[0] = "8"
            ans[-1] = "8"
            return "".join(ans)

        if k == 4: 
            #for n == 1,2
            ans[0] = "8"
            ans[-1] = "8"
            if n > 2: # 2nd last digit has to be 88
                ans[1] = "8"
                ans[-2] = "8"

        elif k == 5:
            ans[0] = "5"
            ans[-1] = "5"

        elif k == 6: #89999...9998
            if n == 1:
                return "6"
            elif n == 2:
                return "66"

            ans[0] = "8"
            ans[-1] = "8"
            sm -= 2

            if sm % 3 != 0:
                if n % 2: #odd middle digit
                    if sm % 3 == 1: #even middle digit
                        ans[n // 2] = str(int(ans[n // 2]) - 1)
                    else: 
                        ans[n // 2] = str(int(ans[n // 2]) - 2)
                else: #even middle digits!!
                    if sm % 3 == 1:
                        ans[n//2] = str(int(ans[n//2]) - 2)
                        ans[n//2-1] = str(int(ans[n//2-1]) - 2)
                    else:
                        ans[n // 2] = str(int(ans[n//2]) - 1)
                        ans[n//2-1] = str(int(ans[n//2-1]) - 1)


        elif k == 7:
            if n % 2: #odd digits
                for i in range(9):
                    if isdivisible7(str("".join(ans))):
                        return "".join(ans)
                    else:
                        ans[n//2] = str(int(ans[n//2]) - 1)
            else:   

                for i in range(9):
                    if isdivisible7(str("".join(ans))):
                        return "".join(ans)
                    else:
                        ans[n//2] = str(int(ans[n//2]) - 1)
                        ans[n//2 - 1] = str(int(ans[n//2 - 1]) - 1) 

        else: 
            if n < 6:
                return "8" * n
            else:
                ans[0] = "8"
                ans[1] = "8"
                ans[2] = "8"
                ans[-1] = "8"
                ans[-2] = "8"
                ans[-3] = "8"

        palindrome = "".join(ans)

        return palindrome
