function isPass(s: string) {
  let v = 0;

  for (let i = 0, l = s.length; i < l; ++i) {
    v = (v * 10 + Number(s[i])) % 7;
  }

  return v === 0;
}

function largestPalindrome(n: number, k: number): string {
  if (n === 1) {
    let r = k;

    while (r + k < 10) {
      r += k;
    }

    return r.toString();
  }

  if (k === 1 || k === 3 || k === 9) {
    return "9".repeat(n);
  }

  if (k === 2) {
    return "8" + "9".repeat(n - 2) + "8";
  }

  if (k === 4) {
    if (n >= 4) {
      return "88" + "9".repeat(n - 4) + "88";
    } else {
      return "8".repeat(n);
    }
  }

  if (k === 5) {
    return "5" + "9".repeat(n - 2) + "5";
  }

  if (k === 6) {
    if (n === 2) {
      return "66";
    }

    if ((n - 2) & 1) {
      return (
        "8" + "9".repeat((n - 2) >> 1) + "8" + "9".repeat((n - 2) >> 1) + "8"
      );
    } else {
      return (
        "8" + "9".repeat((n - 4) >> 1) + "77" + "9".repeat((n - 4) >> 1) + "8"
      );
    }
  }

  if (k === 7) {
    for (let i = 9999; i >= 0; --i) {
      const inn = i.toString().length;

      if (inn * 2 - 1 > n) {
        continue;
      }

      const need = ((n + (n & 1)) >> 1) - inn;
      let s =
        "9".repeat(need) +
        i.toString() +
        i
          .toString()
          .split("")
          .reverse()
          .join("")
          .slice(n & 1) +
        "9".repeat(need);

      if (isPass(s)) {
        return s;
      }
    }
  }

  if (k === 8) {
    if (n >= 6) {
      return "888" + "9".repeat(n - 6) + "888";
    } else {
      return "8".repeat(n);
    }
  }
}
