class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in [1, 3, 9]:
            return '9' * n
        if k == 2:
            return '8' if n == 1 else '8' + '9' * (n - 2) + '8'
        if k == 4:
            return '8' * n if n <= 4 else '88' + '9' * (n - 4) + '88'
        if k == 5:
            return '5' if n == 1 else '5' + '9' * (n - 2) + '5'
        if k == 6:
            if n < 3:
                return '6' * n
            length = (n - 3) >> 1
            if n & 1:
                return '8' + '9' * length + '8' + '9' * length + '8'
            else:
                return '8' + '9' * length + '77' + '9' * length + '8'
        if k == 7:
            if n < 3:
                return '7' * n
            length = (n - 1) >> 1
            if n & 1:
                unit, save = 1, 0, 
                for i in range(length):
                    save = (save + unit * 9) % 7
                    unit = unit * 10 % 7
                middle = unit
                unit = unit * 10 % 7
                for i in range(length):
                    save = (save + unit * 9 % 7) % 7
                    unit = unit * 10 % 7
                for digit in range(9, -1, -1):
                    if not (save + middle * digit) % 7:
                        break
                return '9' * length + str(digit) + '9' * length
            else:
                unit, save = 1, 0, 
                for i in range(length):
                    save = (save + unit * 9) % 7
                    unit = unit * 10 % 7
                middle = unit * 11 % 7
                unit = unit * 100 % 7
                for i in range(length):
                    save = (save + unit * 9 % 7) % 7
                    unit = unit * 10 % 7
                for digit in range(9, -1, -1):
                    if not (save + middle * digit) % 7:
                        break
                return '9' * length + str(digit) * 2 + '9' * length
        if k == 8:
            return '8' * n if n < 7 else '888' + '9' * (n - 6) + '888'

        # 1: '9' * n
        # 2: '8' or '8' + '9' * (n - 2) + '8'
        # 3: '9' * n
        # 4: '8' or '88' or '888' + '88' + '9' * (n - 4) + '88'
        # 5: '5' or '5' + '9' * (n - 2) + '5'
        # 6: '6' or '66' or '8' + '9' * i + 'one or two digits' + '9' * i + '8'
        # 7: '7' or '77' or '959' or '9779' or '99799'
        # 8: '8' or '88' or '888' or '8888' or '88888' or '888888' or '888' + '9' * (n - 6) + '888'
        # 9: '9' * n
