import sys

sys.set_int_max_str_digits(10**7)
class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if n <= 5:
            for x in range(int("9"*n), 0, -1):
                sx = str(x)
                if x % k == 0 and sx == sx[::-1]:
                    return sx

        if k == 1 or k == 3 or k == 9:
            return "9"*n

        if k == 2:
            return "8" + (("9")*(n-2)) + "8"

        if k == 4:
            return "88" + ("9"*(n-4)) + "88"

        if k == 5:
            return "5" + (("9")*(n-2)) + "5"

        if k == 6:
            if n % 2 == 0:
                nines = (n-4) // 2
                for mid in range(9, 0, -1):
                    res = "8" + ("9"*nines) + (str(mid)*2) + ("9"*nines) + "8"
                    resi = int(res)
                    if resi % 6 == 0:
                        return res
            else:
                nines = (n-3) // 2
                for mid in range(9, 0, -1):
                    res = "8" + ("9"*nines) + (str(mid)*1) + ("9"*nines) + "8"
                    resi = int(res)
                    if resi % 6 == 0:
                        return res

        if k == 8:
            return "888" + ("9"*(n-6)) + "888"

        if k == 7:

            if n % 2 == 0:
                nines = (n-2) // 2
                for mid in range(9, 0, -1):
                    res = ("9"*nines) + (str(mid)*2) + ("9"*nines)
                    resi = int(res)
                    if resi % 7 == 0:
                        return res
            else:
                nines = (n-1) // 2
                for mid in range(9, 0, -1):
                    res = ("9"*nines) + (str(mid)*1) + ("9"*nines)
                    resi = int(res)
                    if resi % 7 == 0:
                        return res
