func largestPalindrome(n int, k int) string {

	cal := func(s string, k int) bool {
		left := 0
		for _, c := range s {
			left = left*10 + int(c-'0')%k
			left %= k
		}
		return left == 0
	}

	switch k {
	case 1, 3, 9:
		{
			return strings.Repeat("9", n)
		}
	case 2:
		{
			if n <= 2 {
				return strings.Repeat("8", n)
			}
			return "8" + strings.Repeat("9", n-2) + "8"
		}
	case 4:
		{
			if n <= 4 {
				return strings.Repeat("8", n)
			}
			return "88" + strings.Repeat("9", n-4) + "88"
		}
	case 5:
		{
			if n <= 2 {
				return strings.Repeat("5", n)
			}
			return "5" + strings.Repeat("9", n-2) + "5"
		}
	case 6:
		{
			if n <= 2 {
				return strings.Repeat("6", n)
			}
			if n%2 == 0 {
				return "8" + strings.Repeat("9", n/2-2) + "77" + strings.Repeat("9", n/2-2) + "8"
			} else {
				return "8" + strings.Repeat("9", n/2-1) + "8" + strings.Repeat("9", n/2-1) + "8"
			}
		}
	case 7:
		{
			if n <= 2 {
				return strings.Repeat("7", n)
			}
			if n%2 == 0 {
				for i := 9; i >= 0; i-- {
					s := strings.Repeat("9", n/2-1) + strconv.Itoa(i*10+i) + strings.Repeat("9", n/2-1)
					if cal(s, 7) {
						return s
					}
				}
				return ""
			} else {
				for i := 9; i >= 0; i-- {
					s := strings.Repeat("9", n/2) + strconv.Itoa(i) + strings.Repeat("9", n/2)
					if cal(s, 7) {
						return s
					}
				}
				return ""
			}
		}
	case 8:
		{
			if n <= 6 {
				return strings.Repeat("8", n)
			}
			return strings.Repeat("8", 3) + strings.Repeat("9", n-6) + strings.Repeat("8", 3)
		}
	default:
		return ""
	}
}
