class Solution {
public:
    inline string work(int k,int re){
        re%=7;
        k%=6;
        int tmp=1;
        while(k--) tmp*=3;
        tmp%=7;
        for(int i=9;i>=1;--i){
            if(i*tmp%7==re){
                char c=i+'0';
                string ans="";
                return ans+c;
            }
        }
        return "";
    }

    inline string work_d(int k,int re){
        re%=7;
        k%=6;
        int tmp=1;
        while(k--) tmp*=3;
        tmp%=7;
        for(int i=9;i>=1;--i){
            if((i*10*tmp+i*tmp)%7==re){
                char c=i+'0';
                string ans="";
                return ans+c+c;
            }
        }
        return "";
    }

    inline int Calc(int k){
        k%=6;
        int tmp=2;
        while(k--) tmp*=3;
        return tmp%7;
    }

    string largestPalindrome(int n, int k) {
        string ans="";
        if(k==1 || k==3 || k==9){
            while(n--) ans+='9';
        }
        else if(k==5){
            if(n==1) ans="5";
            else if(n==2) ans="55";
            else{
                ans="5";
                for(int i=1;i<=n-2;++i) ans+="9";
                ans+="5";
            }
        }
        else if(k==2){
            if(n==1) ans="8";
            else if(n==2) ans="88";
            else{
                ans="8";
                for(int i=1;i<=n-2;++i) ans+="9";
                ans+="8";
            }
        }
        else if(k==4){
            if(n<=4){
                while(n--) ans+="8";
            }
            else{
                ans="88";
                for(int i=1;i<=n-4;++i) ans+="9";
                ans+="88";
            }
        }
        else if(k==8){
            if(n<=6){
                while(n--) ans+="8";
            }
            else{
                ans="888";
                for(int i=1;i<=n-6;++i) ans+="9";
                ans+="888";
            }
        }
        else if(k==6){
            if(n==1) ans="6";
            else if(n==2) ans="66";
            else if(n==3) ans="888";
            else if(n==4) ans="8778";
            else if(n==5) ans="89898";
            else if(n==6) ans="897798";
            else{
                if(n&1){
                    ans="8";
                    for(int i=1;i<=n/2-1;++i) ans+="9";
                    ans+="8";
                    for(int i=1;i<=n/2-1;++i) ans+="9";
                    ans+="8";
                }
                else{
                    ans="8";
                    for(int i=1;i<=n/2-2;++i) ans+="9";
                    ans+="77";
                    for(int i=1;i<=n/2-2;++i) ans+="9";
                    ans+="8";
                }
            }
        }
        else if(k==7){
            if(n==1) ans="7";
            else if(n==2) ans="77";
            else{
                if(n&1){
                    int sum=0;
                    for(int i=0;i<n;++i){
                        if(i==(n-1)/2) continue;
                        sum+=Calc(i);
                        sum%=7;
                    }
                    for(int i=1;i<=(n-1)/2;++i) ans+="9";
                    ans+=work((n-1)/2,7-sum);
                    for(int i=1;i<=(n-1)/2;++i) ans+="9";
                }
                else{
                    int sum=0;
                    for(int i=0;i<n;++i){
                        if(i==n/2 || i==n/2-1) continue;
                        sum+=Calc(i);
                        sum%=7;
                    }
                    for(int i=1;i<n/2;++i) ans+="9";
                    ans+=work_d(n/2-1,7-sum);
                    for(int i=1;i<n/2;++i) ans+="9";
                }

            }
        }
        return ans;
    }
};
