class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:

        if k == 1 or k==3 or k==9:
            return "9" * n

        if k == 2:
            if n==1:
                return '8'
            return "8" + "9" * (n - 2) + "8"

        if k==4:
            if n==1:
                return "8"
            if n==2:
                return "88"
            if n==3:
                return "888"
            return "88"+"9"*(n-4)+"88"

        if k == 5:
            if n==1:
                return "5"
            return "5" + "9" * (n - 2) + "5"

        if k==8:
            if n==1:
                return "8"
            if n==2:
                return "88"
            if n==3:
                return "888"
            if n==4:
                return "8888"
            if n==5:
                return "88888"

            return "888"+"9"*(n-6)+"888"

        if k==6:
            if n==1:
                return "6"
            elif n==2:
                return "66"

            l = list(self.largestPalindrome(n,2))
            t = n//2
            r = 0
            m = 1
            for i in range(n):
                r += (int(l[i])*m)%6 
                m = (m*10)%6
            if n%2==1:    
                m = pow(10,t,6)
                for i in range(9,-1,-1):
                    if (r-int(l[t])*m+i*m)%6==0:
                        l[t] = str(i)
                        return "".join(l)
            else:
                m = pow(10,t,6)+pow(10,t-1,6)
                for i in range(9,-1,-1):
                    if (r-int(l[t])*m+i*m)%6==0:
                        l[t] = l[t-1] = str(i)
                        return "".join(l)

        elif k==7:
            if n==1:
                return "7"
            elif n==2:
                return "77"

            l = ["9"]*n
            t = n//2
            r = 0
            m = 1
            for i in range(n):
                r += (9*m)%7
                m = (m*10)%7
            if n%2==1:    
                m = pow(10,t,7)
                for i in range(9,-1,-1):
                    if (r-int(l[t])*m+i*m)%7==0:
                        l[t] = str(i)
                        return "".join(l)
            else:
                m = pow(10,t,7)+pow(10,t-1,7)
                for i in range(9,-1,-1):
                    if (r-int(l[t])*m+i*m)%7==0:
                        l[t] = l[t-1] = str(i)

                        return "".join(l)
