class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k==1 or k==3 or k==9:
            return '9'*n
        if k==5:
            if n==1:
                return '5'
            return '5'+'9'*(n-2)+'5'
        if k==2:
            if n==1:
                return '8'
            return '8'+'9'*(n-2)+'8'
        if k==4:
            if n<=4:
                return '8'*n
            else:
                return '88'+ (n-4)*'9' +'88'
        if k==8:
            if n<=6:
                return '8'*n
            else:
                return '888'+ (n-6)*'9' +'888'
        if k==6:
            if n==1 or n==2:
                return '6'*n
            if n%2:
                return '8'+'9'*((n-3)//2)+'8'+'9'*((n-3)//2)+'8'
            else:
                return '8'+'9'*((n-4)//2)+'77'+'9'*((n-4)//2)+'8'
        if k==7:
            def check(s):
                m = 0
                x = 1
                for i in range(len(s)-1,-1,-1):
                    m = (m + int(s[i])*x)%7
                    x = (x*10)%7
                return m == 0
            if n==1 or n==2:
                return '7'*n
            if n%2:
                x='9'*((n-1)//2)
                ans=0
                for i in range(10):
                    if check(x+str(i)+x):
                        ans=(x+str(i)+x)
                return ans
            else:
                x='9'*((n-2)//2)
                ans=0
                for i in range(10):
                    if check(x+str(i)+str(i)+x):
                        ans=(x+str(i)+str(i)+x)
                return ans
