class Solution {
public:
    bool CanDivide(string num, int digit)
    {
        int remainder = 0;
        string reversedNum = num;

        reverse(reversedNum.begin(), reversedNum.end());

        for (auto c : reversedNum) {
            int currentDigit = (c - '0') + remainder * 10;
            remainder = currentDigit % digit;
        }
        return remainder == 0;
    }
    string largestPalindrome(int n, int k)
    {
        int i;
        string ans;

        if (k == 1 || k == 3 || k == 9) {
            ans.assign(n, '9');
        }
        if (k == 2) {
            ans.assign(n, '9');
            ans[0] = '8';
            ans[n - 1] = '8';
        }
        if (k == 4) {
            if (n <= 4) {
                ans.assign(n, '8');
            } else {
                ans.assign(n, '9');
                ans[0] = '8';
                ans[1] = '8';
                ans[n - 1] = '8';
                ans[n - 2] = '8';
            }
        }
        if (k == 5) {
            ans.assign(n, '9');
            ans[0] = '5';
            ans[n - 1] = '5';
        }
        if (k == 6) {
            if (n <= 2) {
                ans.assign(n, '6');
            } else {
                ans.assign(n, '9');
                ans[0] = '8';
                ans[n - 1] = '8';
                if (n % 2 == 1) {
                    ans[n / 2] = '8';
                } else {
                    ans[n / 2 - 1] = '7';
                    ans[n / 2] = '7';
                }
            }
        }
        if (k == 7) {
            if (n <= 2) {
                ans.assign(n, '7');
            } else {
                ans.assign(n, '9');
                for (i = 9; i >= 1; i--) {
                    if (n % 2 == 1) {
                        ans[n / 2] = i + '0';
                    } else {
                        ans[n / 2 - 1] = i + '0';
                        ans[n / 2] = i + '0';
                    }
                    if (CanDivide(ans, 7)) {
                        break;
                    }
                }
            }
        }
        if (k == 8) {
            if (n <= 6) {
                ans.assign(n, '8');
            } else {
                ans.assign(n, '9');
                ans[0] = '8';
                ans[1] = '8';
                ans[2] = '8';
                ans[n - 1] = '8';
                ans[n - 2] = '8';
                ans[n - 3] = '8';
            }
        }
        return ans;
    }
};
