class Solution {
public:
    string largestPalindrome(int n, int k) {
        string res;
        if (k == 1 || k == 3 || k == 9) {
            for (int i = 0; i < n; ++i) {
                res.push_back('9');
            }
        } else if (k == 2) {
            if (n == 1) {
                res.push_back('8');
                return res;
            }
            res.push_back('8');
            for (int i = 1; i < n - 1; ++i) {
                res.push_back('9');
            }
            res.push_back('8');
        } else if (k == 4) {
            if (n <= 4) {
                for (int i = 0; i < n; ++i) {
                    res.push_back('8');
                }
                return res;
            }
            res.push_back('8');
            res.push_back('8');
            for (int i = 2; i < n - 2; ++i) {
                res.push_back('9');
            }
            res.push_back('8');
            res.push_back('8');
        } else if (k == 5) {
            if (n == 1) {
                res.push_back('5');
                return res;
            }
            res.push_back('5');
            for (int i = 1; i < n - 1; ++i) {
                res.push_back('9');
            }
            res.push_back('5');
        } else if (k == 6) {
            if (n == 1) {
                res.push_back('6');
                return res;
            }
            if (n == 2) {
                res.push_back('6');
                res.push_back('6');
                return res;
            }
            res.push_back('8');
            for (int i = 1; i < (n - 1) / 2; ++i) {
                res.push_back('9');
            }
            if (n % 2 == 0) {
                res.push_back('7');
                res.push_back('7');
            } else {
                res.push_back('8');
            }
            for (int i = n / 2 + 1; i < n - 1; ++i) {
                res.push_back('9');
            }
            res.push_back('8');
        } else if (k == 7) {
            if (n <= 2) {
                for (int i = 0; i < n; ++i) {
                    res.push_back('7');
                }
                return res;
            }
            for (int i = 0; i < n; ++i) {
                res.push_back('9');
            }
            if (n % 2 == 0) {
                for (char i = '9'; i >= '0'; --i) {
                    res[n / 2 - 1] = i;
                    res[n / 2] = i;
                    if (div7(res)) {
                        return res;
                    }
                }
            } else {
                for (char i = '9'; i >= '0'; --i) {
                    res[n / 2] = i;
                    if (div7(res)) {
                        return res;
                    }
                }
            }
        } else if (k == 8) {
            if (n <= 6) {
                for (int i = 0; i < n; ++i) {
                    res.push_back('8');
                }
                return res;
            }
            res.push_back('8');
            res.push_back('8');
            res.push_back('8');
            for (int i = 3; i < n - 3; ++i) {
                res.push_back('9');
            }
            res.push_back('8');
            res.push_back('8');
            res.push_back('8');
        }
        return res;
    }

private:
    static bool div7(const string& s) {
        int count = s[0] - '0';
        count %= 7;
        for (int i = 1; i < s.length(); ++i) {
            count = count * 3 + (s[i] - '0');
            count %= 7;
        }
        return count == 0;
    }
};
