#include <bits/stdc++.h>
using namespace std;
class Solution {
public:
    bool divby7(string n){
        int num = 0;
        for(int i = 0; i<n.size(); i++){
            num = num*10 + (n[i]-'0');
            num = num%7;
        }
        return num==0;
    }
    string largestPalindrome(int n, int k) {
        string s(n, '9');
        if(k==1) return s;
        if(k==2) {
            s[n-1] = '8';
            s[0] = '8';
            return s;
        }
        if(k==3) {
           return s;
        }
        if(k==4) {
            s[n-1] = '8';
            s[0] = '8';
            if(n>=2) s[n-2] = '8';
            if(n>=2) s[1] = '8';
            return s;
        }
        if(k==5) {
            s[n-1] = '5';
            s[0] = '5';
            return s;
        }
        if(k==6) {
            if(n==1) return "6";
            if(n==2) return "66";
            s[0] = '8';
            s[n-1] = '8';
            if(n%2==1) {
                s[n/2]='8';
            }
            else{
                s[n/2]='7';
                s[(n-2)/2]='7';
            }
            return s;
        }
        if(k==8){
            s[n-1] = '8';
            s[0] = '8';
            if(n>=2) s[n-2] = '8';
            if(n>=2) s[1] = '8';
            if(n>=3) s[n-3] = '8';
            if(n>=3) s[2] = '8';
            return s;
        }
        if(k==9){
            return s;
        }
        if(k==7){
            if(n%2==0){
                for(int i = 9; i>=0; i--){
                s[n/2]='0'+i;
                s[(n-2)/2]='0'+i;
                if(divby7(s)) return s;
                }
            }
            else{
                for(int i = 9; i>=0; i--){
                s[n/2]='0'+i;
                if(divby7(s)) return s;
                }
            }
        }
        return "2";
    }
};
