class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        # Enumerate k form 1 to 9
        if k == 1 or k == 3 or k == 9:
            return '9' * n

        if k == 2:
            if n <= 2:
                return '8' * n
            else:
                return '8' + '9' * (n - 2) + '8'

        if k == 5:
            if n <= 2:
                return '5' * n
            else:
                return '5' + '9' * (n - 2) + '5'

        if k == 4:
            if n <= 4:
                return '8' * n
            else:
                return '88' + '9' * (n - 4) + '88'

        if k == 8:
            if n <= 6:
                return '8' * n
            else:
                return '888' + '9' * (n - 6) + '888'

        if k == 6:
            if n <= 2:
                return '6' * n
            elif n == 3:
                return '888'
            elif n == 4:
                return '8778'
            elif n % 2 == 1:
                return '8' + '9' * ((n - 3) // 2) + '8' + '9' * ((n - 3) // 2) + '8'
            else:
                 return '8' + '9' * ((n - 4) // 2) + '77' + '9' * ((n - 4) // 2) + '8'

        if k == 7:
            if n <= 2:
                return '7' * n
            elif n % 6 == 0:
                return '9' * n
            else:
                for mid in range(8, -1, -1):
                    if n % 2 == 0:
                        numstr = '9' * (n // 2 - 1) + str(mid) + str(mid) + '9' * (n // 2 - 1)
                    else:
                        numstr = '9' * (n // 2) + str(mid) + '9' * (n // 2)
                    rem = 0
                    for ch in numstr:
                        rem = (rem * 10 + int(ch)) % 7
                    if rem == 0:
                        return numstr

        # Impossible
        return ''
