class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return '9' * n
        if k == 2:
            if n == 1:
                return '8'
            return '8' + '9' * (n - 2) + '8'
        if k == 3:
            return '9' * n
        if k == 4:
            if n < 5:
                return '8' * n
            return '88' + '9' * (n - 4) + '88'
        if k == 5:
            if n == 1:
                return '5'
            return '5' + '9' * (n - 2) + '5'
        if k == 6:
            if n == 1:
                return '6'
            if n == 2:
                return '66'
            t = list('8' + '9' * (n - 2) + '8')
            if n % 2 == 1:
                t[n // 2] = '8'
            else:
                t[n // 2 - 1] = '7'
                t[n // 2] = '7'
            return ''.join(t)
        if k == 7:
            t = list('9' * n)
            mid = (n - 1) // 2
            if n % 12 == 1:
                t[mid] = '7'
            elif n % 12 == 2:
                t[mid] = '7'
                t[mid + 1] = '7'
            elif n % 12 == 3:
                t[mid] = '5'
            elif n % 12 == 4:
                t[mid] = '7'
                t[mid + 1] = '7'
            elif n % 12 == 5:
                t[mid] = '7'
            elif n % 12 == 7:
                t[mid] = '4'
            elif n % 12 == 8:
                t[mid] = '4'
                t[mid + 1] = '4'
            elif n % 12 == 9:
                t[mid] = '6'
            elif n % 12 == 10:
                t[mid] = '4'
                t[mid + 1] = '4'
            elif n % 12 == 11:
                t[mid] = '4'
            return ''.join(t)
        if k == 8:
            if n < 7:
                return '8' * n
            return '888' + '9' * (n - 6) + '888'
        if k == 9:
            return '9' * n
