class Solution {
public:
    string largestPalindrome(int n, int k) {
        if (k == 1 || k == 3 || k == 9) {
            return string(n, '9');
        }
        if (k == 2) {
            string s(n, '9');
            s[0] = '8';
            s[n-1] = '8';
            return s;
        }
        if (k == 4) {
            if (n <= 4) {
                return string(n, '8');
            }
            return "88" + string(n-4, '9') + "88";
        }
        if (k == 5) {
            string s(n, '9');
            s[0] = '5';
            s[n-1] = '5';
            return s;
        }
        if (k == 6) {
            if (n == 1) {
                return "6";
            }
            if (n == 2) {
                return "66";
            }
            if (n % 2 == 1) {
                string s(n, '9');
                s[0] = '8';
                s[n-1] = '8';
                s[n/2] = '8';
                return s;
            }
            string s(n, '9');
            s[0] = '8';
            s[n-1] = '8';
            s[n/2-1] = '7';
            s[n/2] = '7';
            return s;
        }
        if (k == 7) {
            if (n == 1) {
                return "7";
            }
            if (n == 2) {
                return "77";
            }
            int t = 0;
            for (int i = 0; i < n; ++i) {
                t *= 10;
                t += 9;
                t %= 7;
            }
            string s (n, '9');
            if (n % 2 == 0) {
                int z = 11;
                for (int i = 1; i < n / 2; ++i) {
                    z *= 10;
                    z %= 7;
                }
                for (int i = 0; i <= 9; ++i) {
                    if (z * i % 7 == t) {
                        s[n/2-1] -= i;
                        s[n/2] -= i;
                        break;
                    }
                }
                return s;
            } else {
                int z = 1;
                for (int i = 0; i < n / 2; ++i) {
                    z *= 10;
                    z %= 7;
                }
                // cout << t << endl;
                // cout << z << endl;
                for (int i = 0; i <= 9; ++i) {
                    if (z * i % 7 == t) {
                        s[n/2] -= i;
                        break;
                    }
                }
                return s;
            }
        }

        if (k == 8) {
            if (n <= 6) {
                return string(n, '8');
            }
            return "888" + string(n-6,'9') + "888";
        }

        return "";
    }
};
