class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in [1, 3, 9]:
            return "9" * n

        if k == 2:
            if n == 1:
                return "8"

            return "8" + "9" * (n - 2) + "8"

        if k == 4:
            if n <= 3:
                return "8" * n

            return "88" + "9" * (n - 4) + "88"

        if k == 5:
            if n <= 2:
                return "5" * n

            return "5" + "9" * (n - 2) + "5"

        if k == 8:
            if n <= 5:
                return "8" * n

            return "888" + "9" * (n - 6) + "888"

        if k == 6:
            if n <= 2:
                return "6" * n

            if n % 2 == 0:
                return "8" + "9" * ((n - 4) // 2) + "77" + "9" * ((n - 4) // 2) + "8"

            return "8" + "9" * ((n - 3) // 2) + "8" + "9" * ((n - 3) // 2) + "8"

        if k == 7:
            if n <= 2:
                return "7" * n

            if n % 6 == 0:
                return "9" * n

            for m1 in range(9, -1, -1):
                if n % 2 == 0:
                    n1 = (n - 2) // 2
                    s1 = "9" * n1 + str(m1) * 2 + "9" * n1
                else:
                    n1 = n // 2
                    s1 = "9" * n1 + str(m1) + "9" * n1

                s = list(s1)

                v = 0
                odd = True
                while len(s) >= 3:
                    a = int(s[-3]) * 100 + int(s[-2]) * 10 + int(s[-1]) 
                    for _ in range(3):
                        s.pop()

                    if odd:
                        v += a
                    else:
                        v -= a

                    odd = not odd

                if s:
                    a = int("".join(s))
                    if odd:
                        v += a
                    else:
                        v -= a

                if abs(v) % 7 == 0:
                    return s1
