class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1 or k == 3 or k == 9:
            return "9"*n
        if n <= 5:
            for i in reversed(range(10**(n-1),10**n)):
                if list(str(i)) != list(reversed(str(i))):
                    continue
                if i%k==0:
                    return str(i)
        if k==6 or k==7:
            for j in reversed(range(1,10)):
                p = 0
                l = []
                for i in range(n):
                    p*=10
                    if i==0 or i==n-1:
                        p += k + 2
                        l.append(str(k + 2))
                    elif (n%2==0 and (i==n//2 or i==n//2-1)) or (n%2==1 and i==n//2):
                        p += j
                        l.append(str(j))
                    else:
                        p += 9
                        l.append("9")
                    p%=k
                if p==0:
                    return "".join(l)
        for j in reversed(range(100,1000)):
            s = str(j)
            if int("".join(reversed(s)))%k!=0:
                continue
            p = 0
            l = []
            for i in range(n):
                p*=10
                if min(i,n-i-1)<=2:
                    p+=int(s[min(i,n-i-1)])
                    l.append(s[min(i,n-i-1)])
                else:
                    p+=9
                    l.append("9")
                p%=k
            if p==0:
                return "".join(l)
