class Solution {
    public String largestPalindrome(int n, int k) {
        if (n == 1) {
            for (int i = 9; i >= 0; i--) {
                if (i % k == 0) {
                    return String.valueOf(i);
                }
            }
        }
        // 返回n位被k整除的最大回文字符串
        // k是1-9
        StringBuilder sb = new StringBuilder();
        if (k == 1 || k == 3 || k == 9) {
            for (int i = 0; i < n; i++) {
                sb.append("9");
            }
            return sb.toString();
        }
        if (k == 2) {
            sb.append("8");
            for (int i = 0; i < n - 2; i++) {
                sb.append("9");
            }
            sb.append("8");
            return sb.toString();
        }
        if (k == 5) {
            sb.append("5");
            for (int i = 0; i < n - 2; i++) {
                sb.append("9");
            }
            sb.append("5");
            return sb.toString();
        }
        // 4 8
        // 4 8
        if (k == 4) {
            if (n == 2) {
                return "88";
            }
            if (n == 3) {
                return "888";
            }
            if (n >= 4) {
                sb.append("88");
                for (int i = 0; i < n - 4; i++) {
                    sb.append("9");
                }
                sb.append("88");
                return sb.toString();
            }
        }
        if (k == 8) {
            if (n == 2) {
                return "88";
            }
            if (n == 3) {
                return "888";
            }
            if (n == 4) {
                return "8888";
            }
            if (n == 5) {
                return "88888";
            }
            if (n == 6) {
                return "888888";
            }
            if (n >= 7) {
                sb.append("888");
                for (int i = 0; i < n - 6; i++) {
                    sb.append("9");
                }
                sb.append("888");
                return sb.toString();
            }
        }
        //6
        if (k == 6) {
            if (n == 2) {
                return "66";
            }
            if (n >= 3) {
                if (n % 2 == 1) {
                    sb.append("8");
                    for (int i = 0; i < n - 2; i++) {
                        if (i == (n - 2) / 2) {
                            sb.append("8");
                        } else {
                            sb.append("9");
                        }
                    }
                    sb.append("8");
                    return sb.toString();
                } else {
                    sb.append("8");
                    for (int i = 0; i < n - 2; i++) {
                        if (i == (n - 2) / 2 || i == (n - 2) / 2 - 1) {
                            sb.append("7");
                        } else {
                            sb.append("9");
                        }
                    }
                    sb.append("8");
                    return sb.toString();
                }


            }
        }
        // 单独看7的情况
        // 个位数 7 9 3 1
        // 考察中间的数字是多少
        // 计算22222222222222222 mod 7
        if (n == 2) {
            return "77";
        }
        for (int i = 0; i < n; i++) {
            sb.append("9");
        }
        // 调整中间1个或者2个
        int mod7 = getMod7(n);
        int finalI = 0;
        for (int i = 0; i < 9; i++) {
            if (n % 2 == 1) {
                // 调整中间
                int d = n / 2;
                int t = get10dMol7(d);
                int i1 = (t * i) % 7;
                if (i1 != mod7) {
                    continue;
                }
                finalI = i;
                int ff = 9 - finalI;
                sb.setCharAt(n / 2, (char) (ff + '0'));
                return sb.toString();
            }
            // 偶数
            // 调整中间
            int d = (n - 2) / 2;
            int t = get10dMol7(d);
            t += get10dMol7(d + 1);
            int i1 = (t * i) % 7;
            if (i1 != mod7) {
                continue;
            }
            finalI = i;
            int ff = 9 - finalI;
            sb.setCharAt((n - 2) / 2 + 1, (char) (ff + '0'));
            sb.setCharAt((n - 2) / 2, (char) (ff + '0'));
            return sb.toString();
        }
        return "";
    }

    private static int get10dMol7(int d) {
        // 减少10^d的mod
        int remain = 3;
        for (int i = 0; i < d - 1; i++) {
            if (remain < 7) {
                remain = remain * 10;
            } else {
                remain = remain % 7;
                i--;
            }
        }
        return remain % 7;
    }

    private static int getMod7(int n) {
        // n个2mod7
        int remain = 2;
        for (int i = 0; i < n - 1; i++) {
            if (remain < 7) {
                remain = remain * 10 + 2;
            } else {
                remain = remain % 7;
                i--;
            }
        }
        return remain % 7;
    }
}
