class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k == 1:
            return ('9'*n)
        if k == 2:
            if n <= 2: return('8'*n)
            else: return('8'+'9'*(n-2)+'8')
        if k == 3:
            return('9'*n)
        if k == 4:
            if n <= 4: return '8'*n
            else: return '88'+'9'*(n-4)+'88'
        if k == 5:
            if n <= 2: return('5'*n)
            else: return('5'+'9'*(n-2)+'5')
        if k == 6:
            if n <= 2: return('6'*n)
            else:
                if n%2:
                    return('8'+'9'*((n-3)//2)+'8'+'9'*((n-3)//2)+'8')
                else:
                    return('8'+'9'*((n-4)//2)+'77'+'9'*((n-4)//2)+'8')
        if k == 7:
            if n <= 2: return('7'*n)
            else:
                if n%2:
                    for c in range(9, -1, -1):
                        cand = '9'*((n-1)//2)+str(c)+'9'*((n-1)//2)
                        tt = 0
                        for j in range(0, n, 3):
                            tt += int(cand[j:j+3][::-1])*(1-2*(j%2))
                        if tt%7 == 0:
                            return(cand)
                else:
                    for c in range(9, -1, -1):
                        cand = '9'*((n-2)//2)+str(10*c+c)+'9'*((n-1)//2)
                        tt = 0
                        for j in range(0, n, 3):
                            tt += int(cand[j:j+3][::-1])*(1-2*(j%2))
                        if tt%7 == 0:
                            return(cand)

        if k == 8:
            if n <= 6: return('8'*n)
            else: return('888'+'9'*(n-6)+'888')
        if k == 9:
            return('9'*n)
