int mod(string a, int b) // 高精度a除以单精度b
{
    int d = 0;
    for (int i = 0; i < a.size(); i++)
        d = (d * 10 + (a[i] - '0')) % b; // 求出余数
    return d;
}


class Solution {
public:
    string largestPalindrome(int n, int k) {
    string s = "";

    if (k == 1 || k == 3 || k == 9)
    {
        for (int i = 1; i <= n; i++)
            s += '9';
    }
    else if (k == 2)
    {
        if (n <= 2)
        {
            for (int i = 1; i <= n; i++)
                s += '8';
        }
        else
        {
            s += '8';
            for (int i = 1; i <= n - 2; i++)
                s += '9';
            s += '8';
        }
    }
    else if (k == 4)
    {
        if (n <= 4)
        {
            for (int i = 1; i <= n; i++)
                s += '8';
        }
        else
        {
            s += "88";
            for (int i = 1; i <= n - 4; i++)
                s += '9';
            s += "88";
        }
    }
    else if (k == 8)
    {
        if (n <= 6)
        {
            for (int i = 1; i <= n; i++)
                s += '8';
        }
        else
        {
            s += "888";
            for (int i = 1; i <= n - 6; i++)
                s += '9';
            s += "888";
        }
    }
    else if (k == 5)
    {
        if (n <= 2)
        {
            for (int i = 1; i <= n; i++)
                s += '5';
        }
        else
        {
            s += '5';
            for (int i = 1; i <= n - 2; i++)
                s += '9';
            s += '5';
        }
    }
    else if (k == 6)
    {
        if (n <= 2)
        {
            for (int i = 1; i <= n; i++)
                s += '6';
        }
        else
        {
            if (n % 2 == 0)
            {
                s += '8';
                for (int i = 1; i <= (n - 4) / 2; i++)
                    s += '9';
                s += "77";
                for (int i = 1; i <= (n - 4) / 2; i++)
                    s += '9';
                s += '8';
            }
            else
            {
                s += '8';
                for (int i = 1; i <= (n - 3) / 2; i++)
                    s += '9';
                s += '8';
                for (int i = 1; i <= (n - 3) / 2; i++)
                    s += '9';
                s += '8';
            }
        }
    }
    else if (k == 7)
    {
        if (n <= 2)
        {
            for (int i = 1; i <= n; i++)
                s += '7';
        }
        else
        {
            if (n % 2 == 0)
            {
                for (int k = '9'; k >= '1'; k--)
                {
                    s = "";
                    for (int i = 1; i <= (n - 2) / 2; i++)
                        s += '9';
                    s += k, s += k;
                    for (int i = 1; i <= (n - 2) / 2; i++)
                        s += '9';
                    if (mod(s, 7) == 0)
                        break;
                }
            }
            else
            {
                for (int k = '9'; k >= '1'; k--)
                {
                    s = "";
                    for (int i = 1; i <= (n - 1) / 2; i++)
                        s += '9';
                    s += k;
                    for (int i = 1; i <= (n - 1) / 2; i++)
                        s += '9';
                    if (mod(s, 7) == 0)
                        break;
                }
            }
        }
    }
    // cout << s << endl;
    return s;
    }
};
