class Solution:
    def largestPalindrome(self, n: int, k: int) -> str:
        if k in [1,3,9]:
            return "9"*n

        if k == 5:
            if n >= 2:
                return "5" + "9"*(n-2) + "5"
            return "5"

        if k == 2:
            if n >= 2:
                return "8" + "9"*(n-2) + "8"

            return "8"
        if k == 4:
            if n >= 4:
                return "88" + "9"*(n-4) + "88"
            return "8" * n
        if k == 8:
            if n >= 6:
                return "888" + "9"*(n-6) + "888"
            return "8" * n

        if k == 6:
            if n <= 2:
                return "6"*n

            if n & 1:
                # odd case
                # n >= 3
                return "8" + "9" * ((n-3)//2) + "8" + "9" * ((n-3)//2) + "8"
            else:
                nine = "9" * ((n-4)//2)
                return "8" + nine + "77" + nine + "8"

        if k == 7:
            if n==1: return "7"
            if n==2: return "77"
            if n==3: return "959" # 137 * 7


            # n=4: 9779 = 1397 * 7
            # n=5: 13997 * 7 "99799"

            arr = [9] * n
            m = (len(arr)+1) // 2

            def calc(arr):
                res = 0
                for i in range(n):
                    res += pow(10, i, 7) * arr[i]
                    res %= 7
                return res

            def to_string(arr):
                return "".join(map(str, arr))


            res = calc(arr)
            if res == 0:
                return to_string(arr)

            for i in range(m)[::-1]:
                for num in range(1, 9):
                    arr[i] = num
                    arr[~i] = num
                    tmp = res
                    res -= pow(10, i, 7) * 9
                    res += pow(10, i, 7) * num

                    j = n-i-1
                    if i != j:
                        res -= pow(10, j, 7) * 9
                        res += pow(10, j, 7) * num

                    res %= 7
                    # print(i, res, to_string(arr))



                    if res == 0:
                        return to_string(arr)

                    arr[i] = 9
                    arr[~i] = 9
                    res = tmp



        return None # invalid input
